mapper.update:
UPDATE quiz
question = #{question}, option1 = #{optionFirst}, option2 = #{optionSecond}, option3 = #{optionThird}, option4 = #{optionFourth}, answer = #{answer}, start_date = #{quizStartDate}, quiz_date = SYSDATE,
WHERE quiz_no = #{quizNo}

UPDATE preview
preview_name = #{previewName}, preview_place = #{previewPlace}, preview_date = #{previewDate}, image_file = #{previewImage}, start_date = #{previewStartDate}, end_date = #{previewEndDate}, winner_count = #{winnerCount}, winner_date = #{winnerDate}, preview_regdate = SYSDATE
WHERE preview_no = #{previewNo}

mapper.sql:

	 SELECT
	 	*
	 FROM quiz




				 AND quiz_no = #{searchKeyword}


 		 		 AND quiz_question = #{searchKeyword}



			ORDER BY quiz_status

mapper.select:
SELECT 	COUNT(*) 
FROM participation
WHERE user_no = #{userNo}
AND preview_no = #{previewNo}

SELECT 	COUNT(*) 
FROM participation
WHERE user_no = #{userNo}
AND quiz_no = #{quizNo}
