mapper.select:
  	SELECT COUNT(*)
  	FROM(	
		SELECT 
		*
		FROM purchase 


					WHERE purchase_Status = 0 and purchase_user_no = #{userNo}



					WHERE purchase_Status = 2 and purchase_user_no = #{userNo} 

		)

mapper.update:
UPDATE purchase
purchase_user_no = #{purchaseUserNo}, purchase_product_No = #{purchaseProductNo}, purchase_product_Quantity = #{purchaseProductQuantity}, purchase_Status = #{purchaseStatus}, purchase_Price = #{purchasePrice}, purchase_Date =#{purchaseDate}, cancel_Date = #{cancelDate},
purchase_no = #{purchaseNo}
